import React, { useState } from 'react'
import { TouchableOpacity, StyleSheet, View } from 'react-native'
import { Text } from 'react-native-paper'
import Background from '../components/Background'
import Logo from '../components/Logo'
import Header from '../components/Header'
import Button from '../components/Button'
import TextInput from '../components/TextInput'
import { auth } from '../firebase'
import { theme } from '../core/theme'
import { useNavigation } from '@react-navigation/core'

const Login = () => {
  const [email, setEmail] = useState('')
  const [mdp, setMdp] = useState('')

  const navigation = useNavigation()

  useEffect(() => {
      const unsubscribe = auth.onAuthStateChanged(user => {
          if(user) {
              navigation.replace("MovieRating++")
          }
      })
      return unsubscribe
  }, [])

  const inscription =() => {
      auth
      .createUserWithEmailAndPassword(email, mdp)
      .then(userCredentials =>{
          const user = userCredentials.user;
          console.log("Inscrit en tant que ",user.email);
      })
      .catch(error => alert(error.message))
  }

  const ident =() =>{
      auth
      .signInWithEmailAndPassword(email,mdp)
      .then(userCredentials =>{
          const user = userCredentials.user;
          console.log("Identifié en tant que ", user.email);
      })
      .catch(error => alert(error.message))
  }

  return (
    <Background>
      <Logo />
      <Header>Welcome back.</Header>
      <TextInput
        label="Email"
        returnKeyType="next"
        value={email}
        onChangeText={text => setEmail(text)}
        autoCapitalize="none"
        autoCompleteType="email"
        textContentType="emailAddress"
        keyboardType="email-address"
      />
      <TextInput
        label="Password"
        returnKeyType="done"
        value={mdp}
        onChangeText={text => setMdp(text)}
        secureTextEntry
      />
      <View style={styles.forgotPassword}>
        <TouchableOpacity
          onPress={() => navigation.navigate('ResetPasswordScreen')}
        >
          <Text style={styles.forgot}>Forgot your password?</Text>
        </TouchableOpacity>
      </View>
      <Button mode="contained" onPress={ident}>
        Login
      </Button>
      <Button mode="contained" onPress={inscription}>
        Register
      </Button>
      <View style={styles.row}>
        <Text>Don’t have an account? </Text>
        <TouchableOpacity onPress={() => navigation.replace('RegisterScreen')}>
          <Text style={styles.link}>Sign up</Text>
        </TouchableOpacity>
      </View>
    </Background>
  )
}

  export default Login


  const styles = StyleSheet.create({
    forgotPassword: {
      width: '100%',
      alignItems: 'flex-end',
      marginBottom: 24,
    },
    row: {
      flexDirection: 'row',
      marginTop: 4,
    },
    forgot: {
      fontSize: 13,
      color: theme.colors.secondary,
    },
    link: {
      fontWeight: 'bold',
      color: theme.colors.primary,
    },
  })
